purchaseOrder.onChange:
const allPurchaseOrderLines = $getGrid('purchaseLines');
// console.log('all purchaseLines: ', allPurchaseOrderLines)
const filteredLines = allPurchaseOrderLines.filter(line => line.purchaseOrder === rowData.purchaseOrder);
// console.log('filtered: ', filteredLines)
const purchaseOrderName = rowData.$purchaseOrder$display
// console.log('purchase order name: ', purchaseOrderName)
if (purchaseOrderName !== null)  {
const parentParts = purchaseOrderName.split('-'); // Split parentId by "-"
    const pONumber = parentParts[0];
    const newId = `${pONumber}.0${filteredLines.length}`

    $dgSetRowVals('purchaseLines', rowData.rowKey, {
        name: newId
    })
}

onRecordCreate:
if(!rowData.name) {

setTimeout(() => {
    console.log('data in here: ', rowData)
    const purchaseLines = $dataGrid('purchaseLines');

    const arrayPurchaseLines = []

    purchaseLinesfiltered = []

    for (const key in purchaseLines) {
    if (purchaseLines.hasOwnProperty(key)) {
      arrayPurchaseLines.push(purchaseLines[key])
    }
  }

  purchaseLinesfiltered = arrayPurchaseLines.filter((line) => line.purchaseOrder === rowData.purchaseOrder)
    const parentId = rowData.$purchaseOrder$display
    const parentParts = parentId.split('-'); // Split parentId by "-"

    const pONumber = parentParts[0];

    const newId = `${pONumber}.0${purchaseLinesfiltered.length}`

    $dgSetRowVals('purchaseLines', rowData.rowKey, {
        name: newId
    })

}, 1000)

}
